:root {
  --font-size: 13;
  --font-weight: normal;
  --font: tewi, Source Code Pro, sans;
  --padding: 2px;
  --shadow: 0 2px 4px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24);

  /* ---------- Tomorrow Night ---------- */

  --fg: #C5C8C6;
  --bg: #282A2E;
  --border: #373B41;
  --main-fg: #81A2BE;
  --accent-fg: #52C196;

